mapper.select:
SELECT base.id baseInfoId, base.`name` houseTitle, base.room_number roomNumber, base.hall_number hallNumber, base.cloakroom_number cloakroomNumber, base.area_covered areaCovered, rent.monthly_rent monthlyRent, image.url, contact.publisher_name publisherName, contact.publisher_identity publisherIdentity, area.superior_id roadId, base.area_id areaId, area.`name` areaName, state.house_publish_state state FROM house_state state INNER JOIN house_base_info base ON state.base_info_id = base.id INNER JOIN house_rent_info rent ON state.base_info_id = rent.base_info_id INNER JOIN house_image image ON state.base_info_id = image.base_info_id INNER JOIN house_contact_info contact ON state.base_info_id = contact.base_info_id INNER JOIN house_area area ON base.area_id = area.id
image.is_default_image = 1
AND state.is_deleted = 0
AND state.is_deleted = #{isDeleted}
AND state.publisher_id=#{publisherId}
AND base.city_id=#{cityId}
AND base.city_id IN
#{cityId}
AND base.area_id=#{areaId}
AND state.house_publish_state=#{state}
LIMIT #{offset},#{limit}

SELECT
COUNT(base.id)
FROM house_state state INNER JOIN house_base_info base ON state.base_info_id = base.id


        state.is_deleted = 0


        state.is_deleted = #{isDeleted}


        AND state.publisher_id=#{publisherId}


        AND base.city_id=#{cityId}


        AND base.city_id IN

            #{cityId}



        AND base.area_id=#{areaId}


        AND state.house_publish_state=#{state}
