SELECT
base.id baseInfoId,
base.`name` houseTitle,
base.room_number roomNumber,
base.hall_number hallNumber,
base.cloakroom_number cloakroomNumber,
base.area_covered areaCovered,
rent.monthly_rent monthlyRent,
image.url,
contact.publisher_name publisherName,
contact.publisher_identity publisherIdentity,
area.superior_id roadId,
base.area_id areaId,
area.`name` areaName,
state.house_publish_state state
FROM house_state state INNER JOIN house_base_info base ON state.base_info_id = base.id
INNER JOIN house_rent_info rent ON state.base_info_id = rent.base_info_id
INNER JOIN house_image image ON state.base_info_id = image.base_info_id
INNER JOIN house_contact_info contact ON state.base_info_id = contact.base_info_id
INNER JOIN house_area area ON base.area_id = area.id
INNER JOIN house_detailed_info AS detailed ON base.id = detailed.base_info_id

    image.is_default_image = 1

        AND state.is_deleted = 0


        AND state.is_deleted = #{isDeleted}


        AND state.publisher_id=#{publisherId}


        AND base.city_id=#{cityId}


        AND base.city_id IN

            #{cityId}



        AND base.area_id=#{areaId}


        AND base.area_id IN

            #{areaId}



        AND rent.monthly_rent >= #{minRent}


        AND rent.monthly_rent  <= #{maxRent}


        AND base.hall_number >= #{hallQuery}


        AND base.room_number >= #{roomQuery}


        AND base.orientation_id=#{orientationId}


        AND detailed.decoration_state=#{decoration}


        AND state.house_publish_state=#{state}


ORDER BY base.hall_number ASC ,base.room_number ASC
LIMIT #{offset},#{limit}

SELECT
COUNT(base.id)
FROM house_state AS state INNER JOIN house_base_info AS base ON state.base_info_id = base.id
INNER JOIN house_rent_info AS rent ON base.id = rent.base_info_id
INNER JOIN house_detailed_info AS detailed ON base.id = detailed.base_info_id


        state.is_deleted = 0


        state.is_deleted = #{isDeleted}


        AND state.publisher_id=#{publisherId}


        AND base.city_id=#{cityId}


        AND base.city_id IN

            #{cityId}



        AND base.area_id=#{areaId}


        AND base.area_id IN

            #{areaId}



        AND rent.monthly_rent >= #{minRent}


        AND rent.monthly_rent  <= #{maxRent}


        AND base.hall_number >= #{hallQuery}


        AND base.room_number >= #{roomQuery}


        AND base.orientation_id=#{orientationId}


        AND detailed.decoration_state=#{decoration}


        AND state.house_publish_state=#{state}

SELECT base.id AS id, base.`name` AS houseTitle, base.room_number, base.hall_number, base.cloakroom_number, base.area_covered, detailed.decoration_state AS decorationState, city.`name` AS childrenCityName, area.`name` AS areaName, area.id AS areaId, rent.monthly_rent AS rent, image.url, promotion.promotion_number, promotion.visitor_number FROM house_base_info AS base LEFT JOIN house_promotion AS promotion ON base.id=promotion.base_info_id INNER JOIN house_image AS image ON base.id=image.base_info_id INNER JOIN house_city AS city ON base.city_id=city.id INNER JOIN house_area AS area ON base.area_id=area.id INNER JOIN house_detailed_info AS detailed ON base.id=detailed.base_info_id INNER JOIN house_rent_info AS rent ON base.id=rent.base_info_id INNER JOIN house_state AS state ON base.id=state.base_info_id WHERE image.is_default_image=1 AND state.is_deleted=0 AND state.house_publish_state=2
AND base.city_id = #{childrenCityId}
AND base.city_id IN
#{cityId}
AND base.area_id IN
#{areaId}
AND base.id=-1
ORDER BY promotion.promotion_number DESC LIMIT 10

SELECT base.id baseInfoId, base.`name` houseTitle, base.room_number roomNumber, base.hall_number hallNumber, base.cloakroom_number cloakroomNumber, base.area_covered areaCovered, rent.monthly_rent monthlyRent, image.url, contact.publisher_name publisherName, contact.publisher_identity publisherIdentity, area.superior_id roadId, base.area_id areaId, area.`name` areaName, state.house_publish_state state FROM house_state state INNER JOIN house_base_info base ON state.base_info_id = base.id INNER JOIN house_rent_info rent ON state.base_info_id = rent.base_info_id INNER JOIN house_image image ON state.base_info_id = image.base_info_id INNER JOIN house_contact_info contact ON state.base_info_id = contact.base_info_id INNER JOIN house_area area ON base.area_id = area.id INNER JOIN house_detailed_info AS detailed ON base.id = detailed.base_info_id
image.is_default_image = 1
AND state.is_deleted = 0
AND state.is_deleted = #{isDeleted}
AND base.id IN
#{baseInfoId}
AND state.house_publish_state=#{state}
